import React, {useState, useRef} from 'react'
import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.css'
// write something
// on click take the value of input
// trigger set state 

function App() {
  const [toDoList, setToDo] = useState([]);
  const [name, setName] = useState('');
  const inputName = useRef(null);

  const handleName = (event) => {
    setName(event.target.value);
  }

  const handleAdd = () => {
    const newList = toDoList.concat({name});
    setToDo(newList);
  }

  const clearList = () => {
    setToDo([]);
    inputName.current.value = '';
  }

  return (
    <div style={{justifyContent: 'center', alignItems: 'center', height: '500px', display: 'flex'}}>
      <div style={{width: '500px', height: '500px', backgroundColor: 'orange', padding}}>
        <h1>My Lists</h1>
        <button onClick={clearList}>
          Clear List
        </button>
        {
          toDoList.map((item,index) => (
            <li key={index}>{item.name}</li>
          ))
        }
        <input type='text' onChange={handleName} ref={inputName}/>
        <button onClick={handleAdd}>Add Item</button>
      </div>
    </div>
  );
}

export default App;
