import React, {useState, useRef} from 'react'
import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.css'

function App() {
  const [toDoList, setToDo] = useState([]);
  const [name, setName] = useState('');
  const inputName = useRef(null);

  const handleName = (event) => {
    setName(event.target.value);
  }

  const handleAdd = () => {
    const newList = toDoList.concat({name});
    setToDo(newList);
  }

  const clearList = () => {
    setToDo([]);
    inputName.current.value = '';
  }

  return (
    <div>
      <div className="mainContainer">
        <div className="todoContainer" style={{width: '500px', height: '500px', backgroundColor: 'orange', padding: '20px'}}>
          <h1 style={{textAlign: 'center'}}>My Lists</h1>
          <button onClick={clearList}>
            Clear List
          </button>
          {
            toDoList.map((item,index) => (
              <li key={index}>{item.name}</li>
            ))
          }
          <input type='text' onChange={handleName} ref={inputName}/>
          <button onClick={handleAdd}>Add Item</button>
        </div>
      </div>

    </div>
  );
}

export default App;
